mapper.update:
UPDATE sys_dict SET
status = #{STATUS_NORMAL}
WHERE id = #{id}

UPDATE sys_dict SET
operator = #{operator.id},
oper_time = #{operTime},
status = #{status}
WHERE id = #{id}

mapper.select:
select * from (select from sys_dict a JOIN sys_dict_type b where a.type = b.value and a.type not in( select DISTINCT s.dict_type from sys_dict_filtration s where s.status='1')
AND a.value LIKE CONCAT('%', #{value}, '%')
AND a.type = #{dictType.value}
AND a.status = #{STATUS_NORMAL}
AND a.label LIKE CONCAT('%', #{label}, '%')
UNION select
from sys_dict a join sys_dict_type b JOIN (select * from sys_dict_filtration sdf where sdf.status='1' and find_in_set(#{channelOrgCode},sdf.channel_org_code) and sdf.include_type='1') c where a.type = b.value and c.dict_id=a.id
AND a.value LIKE CONCAT('%', #{value}, '%')
AND a.type = #{dictType.value}
AND a.status = #{STATUS_NORMAL}
AND a.label LIKE CONCAT('%', #{label}, '%') /**已经配置的不包含数据*/ UNION select from sys_dict a join sys_dict_type b where a.type = b.value and a.type in(select DISTINCT s.dict_type from sys_dict_filtration s where s.status='1' ) and a.type not in (select DISTINCT sdf.dict_type from sys_dict_filtration sdf where sdf.status='1' and find_in_set(#{channelOrgCode},sdf.channel_org_code) and sdf.include_type='1') and a.id not in(select sdf.dict_id from sys_dict_filtration sdf where sdf.status='1' and find_in_set(#{channelOrgCode},sdf.channel_org_code) and sdf.include_type='0' and sdf.dict_type not in ( select DISTINCT sdf.dict_type from sys_dict_filtration sdf where sdf.status='1' and find_in_set(#{channelOrgCode},sdf.channel_org_code) and sdf.include_type='1'))
AND a.value LIKE CONCAT('%', #{value}, '%')
AND a.type = #{dictType.value}
AND a.status = #{STATUS_NORMAL}
AND a.label LIKE CONCAT('%', #{label}, '%')
) a ORDER BY a.dictTypeVal,a.parent,a.value,a.sort,a.oper_time DESC

