mapper.update:
UPDATE sys_organization SET
sort = #{sort}
WHERE id = #{id}

mapper.select:
SELECT a.id, a.parent_id AS "parent.id", a.parent_ids, a.code, a.name, a.sort, a.type, a.grade, a.address, a.zip_code, a.master, a.phone, a.fax, a.email FROM sys_organization a WHERE a.status = #{STATUS_NORMAL}
AND a.code IN ${manageChannel}
ORDER BY a.code
